create table fc_pm.users (
    id             number
        generated by default on null as identity
    not null enable,
    username       varchar2(50 char) not null enable,
    email          varchar2(100 char) not null enable,
    password_hash  varchar2(255 char) not null enable,
    first_name     varchar2(55 char),
    last_name      varchar2(55 char),
    created_at     timestamp(6) default on null current_timestamp not null enable,
    updated_at     timestamp(6) default on null current_timestamp not null enable,
    last_active_at timestamp(6),
    is_active      char(1 byte) default 'Y'
);

alter table fc_pm.users
    add
        check ( is_active in ( 'Y', 'N' ) ) enable;

alter table fc_pm.users add constraint users_email_uk unique ( email )
    using index enable;

alter table fc_pm.users
    add constraint users_pk primary key ( id )
        using index enable;

alter table fc_pm.users add constraint users_username_uk unique ( username )
    using index enable;


